import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Button,
  Paper,
  CircularProgress,
  Grid,
} from '@mui/material';

function Results() {
  const location = useLocation();
  const navigate = useNavigate();
  const { score = 0, total = 5 } = location.state || {};
  const percentage = Math.round((score / total) * 100);

  const getFeedback = () => {
    if (percentage >= 80) {
      return {
        message: "Excellent! You're a cybersecurity expert!",
        emoji: "🏆",
      };
    } else if (percentage >= 60) {
      return {
        message: "Good job! You have a solid understanding of cybersecurity.",
        emoji: "👍",
      };
    } else {
      return {
        message: "Keep learning! Cybersecurity is an ongoing journey.",
        emoji: "📚",
      };
    }
  };

  const feedback = getFeedback();

  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        gap: 4,
        maxWidth: 'md',
        mx: 'auto',
        p: { xs: 2, sm: 3 },
      }}
    >
      <Typography
        variant="h2"
        component="h1"
        align="center"
        sx={{
          mb: 2,
          fontSize: { xs: '2rem', sm: '3rem' },
        }}
      >
        Quiz Results
      </Typography>

      <Paper
        elevation={3}
        sx={{
          p: { xs: 3, sm: 4 },
          width: '100%',
          borderRadius: 2,
          textAlign: 'center',
        }}
      >
        <Box
          sx={{
            position: 'relative',
            display: 'inline-flex',
            mb: 3,
          }}
        >
          <CircularProgress
            variant="determinate"
            value={percentage}
            size={120}
            thickness={4}
            sx={{
              color: percentage >= 60 ? 'success.main' : 'warning.main',
            }}
          />
          <Box
            sx={{
              top: 0,
              left: 0,
              bottom: 0,
              right: 0,
              position: 'absolute',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
          >
            <Typography
              variant="h4"
              component="div"
              color="text.secondary"
            >
              {percentage}%
            </Typography>
          </Box>
        </Box>

        <Typography variant="h5" gutterBottom>
          {feedback.emoji} {feedback.message}
        </Typography>

        <Typography variant="h6" color="text.secondary" gutterBottom>
          You scored {score} out of {total} questions correctly
        </Typography>

        <Grid
          container
          spacing={2}
          sx={{
            mt: 3,
            justifyContent: 'center',
          }}
        >
          <Grid item xs={12} sm={6}>
            <Button
              variant="contained"
              color="primary"
              fullWidth
              onClick={() => navigate('/quiz')}
              sx={{ mb: { xs: 2, sm: 0 } }}
            >
              Try Again
            </Button>
          </Grid>
          <Grid item xs={12} sm={6}>
            <Button
              variant="outlined"
              color="primary"
              fullWidth
              onClick={() => navigate('/')}
            >
              Back to Home
            </Button>
          </Grid>
        </Grid>
      </Paper>
    </Box>
  );
}

export default Results;
