import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  Box,
  Typography,
  Button,
  Paper,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormControl,
  LinearProgress,
  Alert,
  Chip,
  Divider,
} from '@mui/material';
import quizData from '../data/questions.json';

function formatTime(seconds) {
  const minutes = Math.floor(seconds / 60);
  const remainingSeconds = seconds % 60;
  return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
}

function calculateTimeBonus(timeSpent, difficulty) {
  const maxTime = {
    beginner: 300, // 5 minutes
    intermediate: 600, // 10 minutes
    advanced: 900, // 15 minutes
  };

  const maxTimeBonus = {
    beginner: 50,
    intermediate: 100,
    advanced: 150,
  };

  const timeLimit = maxTime[difficulty];
  const maxBonus = maxTimeBonus[difficulty];

  if (timeSpent > timeLimit) return 0;
  return Math.round((1 - timeSpent / timeLimit) * maxBonus);
}

function QuizPage() {
  const navigate = useNavigate();
  const location = useLocation();
  const difficulty = location.state?.difficulty || 'beginner';
  const questions = quizData.questions[difficulty];
  
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [score, setScore] = useState(0);
  const [showExplanation, setShowExplanation] = useState(false);
  const [isAnswered, setIsAnswered] = useState(false);
  const [earnedPoints, setEarnedPoints] = useState(0);
  const [timeSpent, setTimeSpent] = useState(0);
  const [quizStartTime] = useState(Date.now());
  const [isQuizComplete, setIsQuizComplete] = useState(false);

  useEffect(() => {
    const timer = setInterval(() => {
      if (!isQuizComplete) {
        setTimeSpent(Math.floor((Date.now() - quizStartTime) / 1000));
      }
    }, 1000);

    return () => clearInterval(timer);
  }, [isQuizComplete, quizStartTime]);

  const handleAnswerSelect = (event) => {
    setSelectedAnswer(parseInt(event.target.value));
  };

  const handleSubmit = () => {
    if (selectedAnswer === null) return;

    setIsAnswered(true);
    if (selectedAnswer === questions[currentQuestion].correctAnswer) {
      setScore(score + 1);
      setEarnedPoints(earnedPoints + questions[currentQuestion].points);
    }
    setShowExplanation(true);
  };

  const handleNext = () => {
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setSelectedAnswer(null);
      setShowExplanation(false);
      setIsAnswered(false);
    } else {
      // Quiz completed
      setIsQuizComplete(true);
      const timeBonus = calculateTimeBonus(timeSpent, difficulty);
      const finalPoints = earnedPoints + timeBonus;

      navigate('/results', {
        state: {
          score,
          total: questions.length,
          difficulty,
          points: finalPoints,
          maxPoints: questions.reduce((sum, q) => sum + q.points, 0),
          timeSpent,
          timeBonus,
        }
      });
    }
  };

  const progress = ((currentQuestion + 1) / questions.length) * 100;

  const getDifficultyColor = () => {
    switch (difficulty) {
      case 'beginner':
        return 'success';
      case 'intermediate':
        return 'warning';
      case 'advanced':
        return 'error';
      default:
        return 'primary';
    }
  };

  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        gap: 3,
        maxWidth: 'md',
        mx: 'auto',
        p: { xs: 2, sm: 3 },
        pb: { xs: '80px', sm: '100px' },
      }}
    >
      <Box sx={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        flexWrap: 'wrap',
        gap: 2
      }}>
        <Chip
          label={difficulty.charAt(0).toUpperCase() + difficulty.slice(1)}
          color={getDifficultyColor()}
          sx={{ fontSize: '1rem', py: 0.5 }}
        />
        <Typography variant="h6" color="text.secondary">
          Points: {earnedPoints}
        </Typography>
        <Chip
          label={`Time: ${formatTime(timeSpent)}`}
          color="primary"
          variant="outlined"
          sx={{ fontSize: '1rem', py: 0.5 }}
        />
      </Box>

      <LinearProgress
        variant="determinate"
        value={progress}
        sx={{ height: 10, borderRadius: 5 }}
      />
      
      <Typography variant="h6" align="center" color="text.secondary">
        Question {currentQuestion + 1} of {questions.length}
      </Typography>

      <Paper
        elevation={3}
        sx={{
          p: { xs: 2, sm: 4 },
          borderRadius: 2,
        }}
      >
        <Typography variant="h5" gutterBottom>
          {questions[currentQuestion].question}
        </Typography>

        {questions[currentQuestion].scenario?.type === 'email' && (
          <Paper
            variant="outlined"
            sx={{
              p: 2,
              mb: 3,
              backgroundColor: 'background.default',
              fontFamily: 'monospace'
            }}
          >
            <Typography variant="body2" gutterBottom>
              <strong>From:</strong> {questions[currentQuestion].scenario.from}
            </Typography>
            <Typography variant="body2" gutterBottom>
              <strong>Subject:</strong> {questions[currentQuestion].scenario.subject}
            </Typography>
            <Divider sx={{ my: 1 }} />
            <Typography
              variant="body2"
              sx={{
                whiteSpace: 'pre-line',
                mt: 1
              }}
            >
              {questions[currentQuestion].scenario.content}
            </Typography>
          </Paper>
        )}

        {questions[currentQuestion].scenario?.type === 'popup' && (
          <Paper
            variant="outlined"
            sx={{
              p: 2,
              mb: 3,
              backgroundColor: '#fff4e5',
              border: '2px solid #ed6c02'
            }}
          >
            <Typography variant="subtitle1" gutterBottom sx={{ color: '#ed6c02', fontWeight: 'bold' }}>
              {questions[currentQuestion].scenario.title}
            </Typography>
            <Typography
              variant="body2"
              sx={{
                whiteSpace: 'pre-line'
              }}
            >
              {questions[currentQuestion].scenario.content}
            </Typography>
          </Paper>
        )}

        {questions[currentQuestion].scenario?.type === 'code_review' && (
          <Paper
            variant="outlined"
            sx={{
              p: 2,
              mb: 3,
              backgroundColor: '#1e1e1e',
              fontFamily: 'monospace'
            }}
          >
            <Typography
              variant="body2"
              sx={{
                whiteSpace: 'pre',
                color: '#d4d4d4',
                overflowX: 'auto'
              }}
            >
              {questions[currentQuestion].scenario.code}
            </Typography>
          </Paper>
        )}

        {questions[currentQuestion].scenario?.type === 'log_analysis' && (
          <Paper
            variant="outlined"
            sx={{
              p: 2,
              mb: 3,
              backgroundColor: '#1e1e1e',
              fontFamily: 'monospace'
            }}
          >
            <Typography
              variant="body2"
              sx={{
                whiteSpace: 'pre-line',
                color: '#d4d4d4'
              }}
            >
              {questions[currentQuestion].scenario.entries?.join('\n') || questions[currentQuestion].scenario.log_entries?.join('\n')}
            </Typography>
          </Paper>
        )}

        {questions[currentQuestion].scenario?.type === 'network_traffic' && (
          <Paper
            variant="outlined"
            sx={{
              p: 2,
              mb: 3,
              backgroundColor: '#f5f5f5'
            }}
          >
            <Typography variant="body2" gutterBottom>
              <strong>Pattern:</strong> {questions[currentQuestion].scenario.pattern}
            </Typography>
            <Typography variant="body2" gutterBottom>
              <strong>Frequency:</strong> {questions[currentQuestion].scenario.frequency}
            </Typography>
            <Typography variant="body2" gutterBottom>
              <strong>Target:</strong> {questions[currentQuestion].scenario.target}
            </Typography>
          </Paper>
        )}

        {questions[currentQuestion].scenario?.type === 'message' && (
          <Paper
            variant="outlined"
            sx={{
              p: 2,
              mb: 3,
              backgroundColor: '#f8f9fa'
            }}
          >
            <Typography variant="body2" gutterBottom>
              <strong>{questions[currentQuestion].scenario.sender}</strong>
            </Typography>
            <Typography
              variant="body2"
              sx={{
                whiteSpace: 'pre-line'
              }}
            >
              {questions[currentQuestion].scenario.content}
            </Typography>
          </Paper>
        )}

        <FormControl component="fieldset" sx={{ width: '100%', mt: 2 }}>
          <RadioGroup value={selectedAnswer} onChange={handleAnswerSelect}>
            {questions[currentQuestion].options.map((option, index) => (
              <FormControlLabel
                key={index}
                value={index}
                control={<Radio />}
                label={option}
                disabled={isAnswered}
                sx={{
                  marginY: 1,
                  padding: 1,
                  borderRadius: 1,
                  '&:hover': {
                    backgroundColor: 'action.hover',
                  },
                }}
              />
            ))}
          </RadioGroup>
        </FormControl>

        {showExplanation && (
          <Alert
            severity={selectedAnswer === questions[currentQuestion].correctAnswer ? "success" : "error"}
            sx={{ mt: 2 }}
          >
            {questions[currentQuestion].explanation}
          </Alert>
        )}

        <Box
          sx={{
            display: 'flex',
            justifyContent: 'space-between',
            mt: 3,
            gap: 2,
            flexDirection: { xs: 'column', sm: 'row' },
          }}
        >
          <Button
            variant="contained"
            color="primary"
            disabled={selectedAnswer === null || isAnswered}
            onClick={handleSubmit}
            fullWidth={true}
            sx={{ order: { xs: 2, sm: 1 } }}
          >
            Submit Answer
          </Button>
          <Button
            variant="outlined"
            color="primary"
            disabled={!isAnswered}
            onClick={handleNext}
            fullWidth={true}
            sx={{ order: { xs: 1, sm: 2 } }}
          >
            {currentQuestion === questions.length - 1 ? 'Finish Quiz' : 'Next Question'}
          </Button>
        </Box>
      </Paper>
    </Box>
  );
}

export default QuizPage;
